        end,
      },
    },
  },
  Bonuses={
    {
      Category="COMBAT",
      Formula=Formula("5"),
      Type={
        Name="Armor",
      },
      Conditions={
        function (character, item)
          return ((character.CountArcaneSpellCastingClasses(1))) >= 1 or (character.Variables["Caster_Level_Highest__Arcane"] >= 1)
        end,
      },
      Variables={
        "AC",
      },
    },
    {
      Category="SAVE",
      Formula=Formula("4"),
      Type={
        Name="Resistance",
      },
      Conditions={
