    Slot="belt",
    ['Construction Cost']="7,450 gp",
    ['Construction Requirements']="Craft Wondrous Item, tongues, creator must be a dwarf",
  },
  Weight=1,
  Key="Belt of Dwarvenkind",
  Description={
    Format="This belt gives the wearer a +4 competence bonus on Charisma checks and Charisma-based skill checks as they relate to dealing with dwarves, a +2 competence bonus on similar checks when dealing with gnomes and halflings, and a -2 competence penalty on similar checks when dealing with anyone else. The wearer can understand, speak, and read Dwarven. If the wearer is not a dwarf, he gains 60-foot darkvision, dwarven stonecunning, a +2 enhancement bonus to Constitution, and a +2 resistance bonus on saves against poison, spells, and spell-like effects.",
  },
  Types={
    "Magic",
    "Wondrous",
    "Belt",
  },
  Bonuses={
    {
      Category="STAT",
      Variables={
        "CON",
      },
      Formula=Formula("2"),
      Conditions={
        function (character)
          return not (((character.Race.Name == "Dwarf%" and 1 or 0)) >= 1)
        end,
      },
    },
    {
      Category="VAR",
      Variables={
        "DarkvisionRange",
      },
      Formula=Formula("60"),
      Type={
        Name="Base",
        Replace=false,
        Stack=false,
      },
    },
  },
  SortKey="Belt of Dwarvenkind",
  Abilities={
    {
      Category="Special Ability",
      Nature="VIRTUAL",
      Names={
