      },
    },
    {
      Category="Equipment",
      Nature="AUTOMATIC",
      Names={
        "Belt of Dwarvenkind ~ Languages",
        "Belt of Dwarvenkind ~ Skill Bonus ~ Dwarves",
        "Belt of Dwarvenkind ~ Skill Bonus ~ Gnomes/Halflings",
        "Belt of Dwarvenkind ~ Skill Penalty ~ Everyone Else",
      },
    },
  },
  SourcePage="p.501",
})
