DefineEquipment({
  Name="Dust of Tracelessness",
  Cost=250,
  Qualities={
    Aura="faint transmutation",
    ['Caster Level']="3rd",
    Slot="none",
    ['Construction Cost']="125 gp",
    ['Construction Requirements']="Craft Wondrous Item, pass without trace",
  },
  Weight=0,
  Key="Dust of Tracelessness",
  Description={
    Format="This normal-seeming dust is actually a magic powder that can conceal the passage of its possessor and his companions. Tossing a handful of this dust into the air causes a chamber of up to 100 square feet of floor space to become as dusty, dirty, and cobwebladen as if it had been abandoned and disused for a decade.&nl;A handful of dust sprinkled along a trail causes evidence of the passage of as many as a dozen men and horses to be obliterated for 250 feet back into the distance. The results of the dust are instantaneous, and no magical aura lingers afterward from this use of the dust. Survival checks made to track a quarry across an area affected by this dust have a DC 20 higher than normal.",
  },
  Types={
    "Magic",
    "Wondrous",
    "Consumable",
  },
  SortKey="Dust of Tracelessness",
  SourcePage="p.510",
})
DefineEquipment({
  Name="Efficient Quiver",
  Cost=1800,
  Contains={
    Capacity=math.huge,
    ContainedItemWeightDoesNotCount=true,
    ItemLimits={
      Arrow=60,
      Bolt=60,
      Wand=60,
      Javelin=18,
      Total=6,
