DefineEquipment({
  Name="Horn of Fog",
  Cost=2000,
  Qualities={
    Aura="faint conjuration",
    ['Caster Level']="3rd",
    Slot="none",
    ['Construction Cost']="1,000 gp",
    ['Construction Requirements']="Craft Wondrous Item, obscuring mist",
  },
  Weight=1,
  Key="Horn of Fog",
  Description={
    Format="This small bugle allows its possessor to blow forth a thick cloud of heavy fog similar to that of an obscuring mist spell. The fog covers a 10-foot square next to the horn blower each round that the user continues to blow the horn; a fog cloud travels 10 feet each round in a straight line from the emanation point unless blocked by something substantial such as a wall. The device makes a deep, foghorn-like noise, with the note dropping abruptly to a lower register at the end of each blast. The fog dissipates after 3 minutes. A moderate wind (11+ mph) disperses the fog in 4 rounds; a strong wind (21+ mph) disperses the fog in 1 round.",
  },
  Types={
    "Magic",
    "Wondrous",
    "Instrument",
    "Wind",
  },
  SortKey="Horn of Fog",
  SourcePage="p.519",
})
DefineEquipment({
  Name="Horn of Goodness/Evil",
  Cost=6500,
  Qualities={
    Aura="moderate abjuration",
    ['Caster Level']="6th",
    Slot="none",
    ['Construction Cost']="3,250 gp",
    ['Construction Requirements']="Craft Wondrous Item, magic circle against good, magic circle against evil",
  },
  Weight=1,
  Key="Horn of Goodness/Evil",
  Description={
    Format="This trumpet adapts itself to its owner, so it produces either a good or an evil effect depending on the owner's alignment. If the owner is neither good nor evil, the horn has no power whatsoever. If he is good, then blowing the horn has the effect of a magic circle against evil. If he is evil, then blowing the horn has the effect of a magic circle against good. In either case, this ward lasts for 1 hour. The horn can be blown once per day.",
  },
  Types={
    "Magic",
    "Wondrous",
    "Instrument",
    "Wind",
  },
  SortKey="Horn of Goodness/Evil",
  SourcePage="p.519",
  SpellLikeAbilities={
    {
      Name="Magic Circle against Good",
      SpellBookName="Horn of Goodness/Evil",
      Times="1",
      TimeUnit="Day",
      CasterLevel="6",
      Conditions={
        function (character)
          return character.Alignment == "LE" or character.Alignment == "NE" or character.Alignment == "CE"
        end,
      },
    },
    {
      Name="Magic Circle against Evil",
      SpellBookName="Horn of Goodness/Evil",
      Times="1",
      TimeUnit="Day",
      CasterLevel="6",
      Conditions={
        function (character)
          return character.Alignment == "LG" or character.Alignment == "NG" or character.Alignment == "CG"
        end,
      },
    },
  },
})
DefineEquipment({
  Name="Horn of Valhalla (Silver)",
  Cost=50000,
  Qualities={
    Aura="strong conjuration",
    ['Caster Level']="13th",
    Slot="none",
    ['Construction Cost']="25,000 gp",
    ['Construction Requirements']="Craft Wondrous Item, summon monster VI",
  },
  Weight=2,
  Key="Horn of Valhalla (Silver)",
  Description={
    Format="This magic instrument comes in four varieties. Each appears to be normal until someone speaks its command word and blows the horn. Then the horn summons a number of human barbarians to fight for the character who summoned them. Each horn can be blown just once every 7 days. The horn's type determines what barbarians are summoned and what prerequisite is needed to use the horn. Any character who uses a horn of Valhalla but doesn't have the prerequisite is attacked by the barbarians she herself summoned.&nl;Summoned barbarians are constructs, not actual people (though they seem to be); they arrive with the starting equipment for barbarians. They attack anyone the possessor of the horn commands them to fight until they or their opponents are slain or until 1 hour has elapsed, whichever comes first.",
  },
  Types={
    "Magic",
    "Wondrous",
    "Instrument",
    "Wind",
  },
  SpecialProperties={
    {
      Format="Once per week can summon 2d4+2 2nd level human barbarians to fight for the character who summoned them.",
    },
  },
  SortKey="Horn of Valhalla, Silver",
  SourcePage="p.519",
})
