  Cost=6000,
  Qualities={
    Aura="faint transmutation",
    ['Caster Level']="3rd",
    Slot="feet",
    ['Construction Cost']="3,000 gp",
    ['Construction Requirements']="Craft Wondrous Item, levitate",
