    },
    {
      Category="VAR",
      Variables={
        "MonkACLVL",
      },
      Formula=Formula("5"),
    },
    {
      Category="VAR",
      Variables={
        "StunningFistAttack",
      },
      Formula=Formula("1"),
      Conditions={
        function (character)
          return 1 <= #filter(character.Abilities, function (ability)
            return ability.Category == "FEAT" and (ability.Name == "Stunning Fist")
          end)
